mapper.select:
select DISTINCT t1.purchase_order_id purchaseOrderId, t1.merged_id mergedId, t1.merchant_id merchantId, t1.person_in_charge personInCharge, t1.order_total_price orderTotalPrice, t1.order_actual_price orderActualPrice, t1.verify_status verifyStatus, t1.remark, t1.del_flag delFlag, t1.order_status orderStatus, t1.create_time createTime, t1.update_time updateTime, t1.create_by createBy, t1.update_by updateBy, t4.real_name realName, t5.smi_name smiName, t5.smi_phone smiPhone, t5.smi_address smiAddress from t_merchant_purchase_order t1
INNER JOIN t_merchant_purchase_orders_detail t2 on t1.purchase_order_id = t2.purchase_order_id and t2.goods_name like '%${goodsName}%' and t2.del_flag = 0
INNER JOIN t_merchant_user_info t3 on t3.smiid = t1.merchant_id INNER JOIN t_merchant_user t4 on t3.muid = t4.guid INNER JOIN t_store_merchant_info t5 on t3.smiid = t5.smiid where t1.del_flag = 0
and t1.create_time >= #{startTime}
and t1.create_time <= #{endTime}
and t1.merchant_id = #{merchantId}
and t1.person_in_charge like '%${personInCharge}%'
limit #{offset}, #{pageSize}

mapper.update:
update t_merchant_purchase_order set order_status = 3, merged_id = #{mergedId}
WHERE purchase_order_id IN

  #{item}
