mapper.select:
select t1.purchase_order_detail_id purchaseOrderDetailId, t1.purchase_order_id purchaseOrderId, t1.goods_id goodsId, t1.goods_name goodsName, t1.order_quantity orderQuantity, t1.actual_quantity actualQuantity, t1.order_price actualPrice, t1.remark, t1.order_detail_status orderDetailStatus, t1.del_flag delFlag, t3.sgi_icon sgiIcon from t_purchase_orders_detail t1 INNER JOIN t_supplier_goods t2 on t1.goods_id = t2.goods_id INNER JOIN ehu.t_store_goods_info t3 on t2.sgiid = t3.sgiid INNER JOIN ehu.t_store_goods_menu_second t4 on t3.sgmsid = t4.sgmsid
and t4.sgmsid = #{secondMenuId}
INNER JOIN ehu.t_store_goods_menu_first t5 on t4.sgmfid = t5.sgmfid
and t5.sgmfid = #{firstMenuId}
WHERE t1.purchase_order_id = #{orderId}
