mapper.select:
select t1.sgiid, t1.supplier_id supplierId, t1.standard, t1.standard_value standardValue, t1.goods_id goodsId, t1.del_flag delFlag, t1.cost_price costPrice, t1.min_order_quantity minOrderQuantity, t2.sgi_name sgiName,t2.sgi_icon sgiIcon from t_supplier_goods t1 INNER JOIN t_store_goods_info t2 on t1.sgiid = t2.sgiid
AND t2.sgi_name like '%${searchText}%'
INNER JOIN ehu.t_store_goods_menu_second t3 on t2.sgmsid = t3.sgmsid
AND t2.sgmsid = #{secondMenuId}
INNER JOIN ehu.t_store_goods_menu_first t4 on t3.sgmfid = t4.sgmfid
AND t3.sgmfid = #{firstMenuId}
WHERE t1.del_flag = 0
AND t1.supplier_id = #{supplierId}
ORDER BY t1.sgiid LIMIT #{offset}, #{pageSize}

select IFNULL(COUNT(t1.goods_id), 0) totalCount from t_supplier_goods t1
INNER JOIN t_store_goods_info t2 on t1.sgiid = t2.sgiid

    AND t2.sgi_name like '%${searchText}%'

INNER JOIN ehu.t_store_goods_menu_second t3 on t2.sgmsid = t3.sgmsid

    AND t2.sgmsid = #{secondMenuId}

INNER JOIN ehu.t_store_goods_menu_first t4 on t3.sgmfid = t4.sgmfid

    AND t3.sgmfid = #{firstMenuId}

WHERE t1.del_flag = 0

    AND t1.supplier_id = #{supplierId}
